import React, { Component } from "react";
import ForgotPassword from "../components";
import axios from "axios";
import { Redirect } from "react-router-dom";

class ForgotPasswordContainer extends Component{

    constructor(props){
        super(props);

        this.state = {
            data:{},
            flag: false
        };
    }

    onSubmitHandler = (event) =>{
        event.preventDefault();
        if((this.state.data.password).localCompare)
        axios.post('http://localhost:8080/user/forgotpassword',this.state.data)
        .then(response =>{
            if(response.data.code == 200){
                this.setState({flag:true});
            }
        })
        .catch(error =>{
            console.log(error);
        })
    }

    onOtpSubmitHandler = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8080/user/generateotp', this.state.data)
        .then(response =>{
            console.log(response);
        })
        .catch(error => {
            console.log(error);
        });
    } 

    onChangeHandler = (event) =>{
        let {data} = this.state;
        data[event.target.name] = event.target.value;
        this.setState(data);
    }

    render(){
        const {data, flag} = this.state;

        if(flag){
            return (
                <Redirect to="/login"/>
            );
        }
        return (
            <ForgotPassword state ={data} onSubmitHandler = {this.onSubmitHandler} onOtpSubmitHandler={this.onOtpSubmitHandler} onChangeHandler = {this.onChangeHandler}/>
        );
    }
}

export default ForgotPasswordContainer;
