import React from "react";

const SearchResult = ({ data, onfollowHandler, isFollowed}) => {
  {console.log(isFollowed)}
  let resultList =  data.map((item, itemIndex) => (
        <div className="col-3">
          <div className="search-result-card shadow">
          <div
            className="card .flip-card-inner "
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
              alignItems:"center",
              marginBottom:"20px",
              borderRadius:"24px"
            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems:"center"
              }}
            >
              <img
                className="search-profile-picture rounded-circle"
                src={item.profilePictureUrl !== ""?item.profilePictureUrl:"https://www.freeiconspng.com/thumbs/profile-icon-png/profile-icon-9.png"}
              ></img>
              <div>
                <p style={{ textTransform: "capitalize", fontSize:"20px", marginTop:"10px"}}><b>{item.name}</b></p>
              </div>
            </div>
            <button className={isFollowed[item.id]?"btn":"buttons2"} style={{width:"140px"}} onClick={() =>onfollowHandler(item.id, itemIndex)}>{isFollowed[item.id]?"Followed":"Follow"}</button>
          </div>
        </div>
        </div>
  ));
  return (
    <div className="container ">
      <div className="row">
          {resultList}
      </div>
    </div>
  );
};

export default SearchResult;
