import React, { Component } from "react";
import Register from ".././Components";
import axios from "axios"
import { Redirect } from "react-router-dom";

class RegisterContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: {},
      loggedIn: false
    };
  }

  onChangehandler = (event) => {
    let { data } = this.state;
    data[event.target.name] = event.target.value;
    this.setState(data);
  };

  onSubmithandler = (event) => {
    event.preventDefault();
    axios.post('http://localhost:8080/user', this.state.data)
    .then(response =>{
        if(response.data.code == 200){
            sessionStorage.setItem("isAuth",true); //local storage 
            sessionStorage.s
            this.setState({loggedIn:true});
        }
    })
    .catch(error => {
        console.log(error);
    });
  };

  onOtpSubmitHandler = (event) => {
    event.preventDefault();
    axios.post('http://localhost:8080/user/generateotp', this.state.data)
    .then(response =>{
        console.log(response);
    })
    .catch(error => {
        console.log(error);
    });
  } 

  render() {
    const  data  = this.state.data;
    console.log(this.state);

    if(this.state.loggedIn){
        return <Redirect to="/profile"/>;
    }
    return (
      <Register
        state={data}
        onChangehandler={this.onChangehandler}
        onSubmithandler={this.onSubmithandler}
        onOtpSubmitHandler = {this.onOtpSubmitHandler}
      />
    );
  }
}
export default RegisterContainer;
