import React from "react";
import { Buttons, TextArea, TextBox } from "../../../shared/components";
import "../../.././assets/index.css";
import SendOutlinedIcon from "@material-ui/icons/SendOutlined";

const Comment = ({postId, data, onCommentSubmitHandler, onChangeHandler}) => {
  return (
    <div className="comment">
      <div>
          <div style={{ display: "flex", flexDirection: "row" }}>
            <div>
              <TextBox
                style={{ borderRadius: "30px", width: "660px" }}
                placeholder="Type Your Comment"
                name="comment"
                value = {data.comment}
                onChange = {onChangeHandler}
              />
            </div>
            <div>
              {/* <Buttons type="submit" value="Add" /> */}
              <Buttons
                value="Send"
                type="submit"
                onClick = {(value) => onCommentSubmitHandler(postId)}
                style={{ fontSize: "35px", marginLeft:"8px", marginTop:"5px" }}
              ></Buttons>
            </div>
          </div>
      </div>
    </div>
  );
};

export default Comment;
