import React from "react";
import {
  Buttons,
  TextBox,
  UploadButton,
  TextArea,
} from "../../../shared/components";
import "../../.././assets/index.css";

const CreatePost = ({data, onSubmitHandler, onChangeHandler, handleUpload, handleChange}) => {
  return (

    <div className="createPost card">
      <ul class="nav nav-tabs">
        <li class="nav-item">
          <form onSubmit={onSubmitHandler}>
            <div className="createPost-text">
              <TextArea
                placeholder="Start Writing Post..."
                style={{height: "100px"}}
                name="text"
                value={data.text}
                onChange={onChangeHandler}
              />
            </div>
            <div className="createPost-buttons">
              <UploadButton onChange={handleChange} style={{color:"crimson"}}/>
              <Buttons type="submit" value="Done" />
            </div>
          </form>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">Link</a>
        </li>
      </ul>
    </div>
  );
};

export default CreatePost;
