import React, { Component } from "react";
import AddComment from "../components/AddComment";
import axios from "axios";
import { Redirect } from "react-router-dom";

class AddCommentContainer extends Component{

    constructor(props){
        super(props);

        this.state = {
            data:{comment:""},
            flag: false
        };
    }

    onSubmitHandler = (postId) =>{
        let details = {userId:sessionStorage.getItem("uid"),comment:data.comment}
        axios.post('http://localhost:8080/post/'+postId+'/comments', details)
        .then(response =>{
            console.log(response)
            if(response.data.code == 200){
                this.setState({flag:true});
            }
        })
        .catch(error =>{
            console.log(error);
        })
    }

    onChangeHandler = (event) => {
        let data = this.state.data;
        data[event.target.name] = event.target.value;
        this.setState({data});
        console.log(data);
    }

    render(){
        const {data, flag} = this.state;
        return (
           <AddComment data ={data} onSubmitHandler = {this.onSubmitHandler} onChangeHandler = {this.onChangeHandler}/>
        );
    }
}

export default AddCommentContainer;
