import React from "react";

const SearchResult = ({ data, onfollowHandler, isFollowed, onViewProfileClickHandler}) => {
  {console.log(isFollowed)}
  let resultList =  data.map((item, itemIndex) => (
        <div className="col-3">
          <div
            className="card search-result-card"
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
              alignItems:"center",
              marginBottom:"20px",
              borderRadius:"24px"
            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems:"center"
              }}
            >
              <img
                className="search-profile-picture rounded-circle"
                src={item.profilePictureUrl !== ""?item.profilePictureUrl:"https://www.freeiconspng.com/thumbs/profile-icon-png/profile-icon-9.png"}
              ></img>
              <div>
                <p onClick = {onViewProfileClickHandler} style={{ textTransform: "capitalize", fontSize:"20px", marginTop:"10px"}} data-bs-toggle="modal" data-bs-target="#staticBackdrop"><b>{item.name}</b></p>
              </div>

              <div class="modal fade" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div class="modal-dialog">
                  <div class="modal-content">
                    <div class="modal-header">
                      <h5 class="modal-title" id="staticBackdropLabel">Modal title</h5>
                      <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                    </div>
                    <div class="modal-body">
                      <p>My name is Khan</p>
                    </div>
                    <div class="modal-footer">
                      <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                      <button type="button" class="btn btn-primary">Understood</button>
                    </div>
                  </div>
                </div>
              </div>

            </div>
            <button className={isFollowed[item.id]?"btn":"buttons2"} style={{width:"140px"}} onClick={() =>onfollowHandler(item.id, itemIndex)}>{isFollowed[item.id]?"Followed":"Follow"}</button>
          </div>
        </div>
  ));
  return (
    <div className="container ">
      <div className="row">
          {resultList}
      </div>
    </div>
  );
};

export default SearchResult;
