import React from "react";
import { Buttons, TextBox } from "../../.././shared/components";
import "../../.././assets/index.css";
import ThumbUpAltOutlinedIcon from "@material-ui/icons/ThumbUpAltOutlined";
import ModeCommentOutlinedIcon from "@material-ui/icons/ModeCommentOutlined";
import { NavLink } from "react-router-dom";
import { AddComments } from "../components";

const Feeds = ({posts, onLikeClickHandle, onCommentClickHandle, flag}) => {
  return (
    posts.map((post,itemIndex) => <div className="postDetails card" style={{ padding: "5px" }}>
      <div>
        <div>
          <p id="postedBy" style={{marginLeft:"5px", marginBottom:"-1px", fontWeight:"300px"}}>post.userName</p>
        </div>
        <div className="postDetails-content card">

          {post.text == null && (
            <div className="postText">
              <p id="text">{post.text}</p>
            </div>
          )}

          {post.title && <img className="postImage"></img>}
        </div>
        <div className="postDetails-button">
          {/* <div>
                        <Buttons value="Like"/>
                    </div>
                    <div style={{marginLeft:"10px"}}>
                        <Buttons value="Comment"/>
                    </div> */}
          <div>
            <ThumbUpAltOutlinedIcon
              id={post.id}
              className="likeButton"
              style={{ marginLeft: "10px", fontSize: "30px" }}
              onClick={(value) => onLikeClickHandle(post.id)}
            ></ThumbUpAltOutlinedIcon>
          </div>

          <div>
            <ModeCommentOutlinedIcon
              className="likeButton"
              style={{ fontSize: "30px", marginLeft: "10px" }}
              onClick ={onCommentClickHandle}
            ></ModeCommentOutlinedIcon>
          </div>
        </div>
        <div>
            {flag && <AddComments/>}
        </div>
      </div>
    </div>
    )
  );
};

export default Feeds;
