import React, { Component } from "react";
import Feeds from "../components/Feeds";
import axios from "axios";
import { Redirect } from "react-router-dom";

class FeedsContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: {},
      posts: [],
      flag: false,
      addComment:{}
    };
  }

  componentDidMount() {
    axios
      .get('http://localhost:8080/post/'+sessionStorage.getItem("uid"))
      .then((response) => {
        console.log(response);
        this.setState({ posts: response.data.data.postsList});
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  onCommentChangeHandler = (postId) => {  
    this.setState({ flag: this.state.flag?false:true });
  };

  onLikeClickHandle = (postId) => {
    const detail = { userId: sessionStorage.getItem("uid") };
    axios
      .post("http://localhost:8080/post/" + postId + "/like", detail)
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    const { data, posts, flag } = this.state;

    return (

      <Feeds
        posts={posts}
        onLikeClickHandle={this.onLikeClickHandle}
        onCommentClickHandle={this.onCommentChangeHandler}
        flag={flag}
      />
    );
  }
}

export default FeedsContainer;
