import React from "react";
import { Buttons, TextBox } from "../../.././shared/components";
import "../../.././assets/index.css";
import ThumbUpAltOutlinedIcon from "@material-ui/icons/ThumbUpAltOutlined";
import ModeCommentOutlinedIcon from "@material-ui/icons/ModeCommentOutlined";
import { NavLink } from "react-router-dom";
import { AddComments } from "../components";

const Feeds = ({
  itemIndex,
  commentSelect,
  liked,
  post,
  onLikeClickHandle,
  onCommentClickHandle,
  onCommentChangeHandler,
  onCommentSubmitHandler,
  addComment,
  flag,

}) => {
  return (
    <div
      id={itemIndex}
      className="postDetails card"
      style={{ padding: "5px", background: "#F0F0F0", borderRadius: "0px" }}
    >
      <div>
        <div>
          <p id="postedBy" style={{ marginLeft: "5px", marginBottom: "-1px" }}>
            <b>{post.userName}</b>
          </p>
        </div>
        <div
          className="postDetails-content card"
          style={{ borderRadius: "0px" }}
        >
          {post.text != "" && (
            <div className="postText">
              <p style={{ fontWeight: "10px" }} id="text">
                {post.text}
              </p>
            </div>
          )}

          {post.imageUrl != "" && <img className="postImage"></img>}
        </div>
        <div className="postDetails-button">
          {/* <div>
                        <Buttons value="Like"/>
                    </div>
                    <div style={{marginLeft:"10px"}}>
                        <Buttons value="Comment"/>
                    </div> */}
          <div>
            <ThumbUpAltOutlinedIcon
              id={itemIndex}
              className="likeButton"
              style={{ marginLeft: "10px", fontSize: "30px" }}
              onClick={(value) => onLikeClickHandle(post.postId, itemIndex)}
            ></ThumbUpAltOutlinedIcon>
          </div>

          <div>
            <ModeCommentOutlinedIcon
              id={itemIndex}
              className="likeButton"
              style={{ fontSize: "30px", marginLeft: "10px" }}
              onClick={(value) => onCommentClickHandle(post.postId, itemIndex)}
            ></ModeCommentOutlinedIcon>
          </div>
        </div>
        <div>
          {commentSelect === itemIndex && (
            <div className="comment">
              <div>
                <div style={{ display: "flex", flexDirection: "row" }}>
                  <div>
                    <TextBox
                      style={{ borderRadius: "30px", width: "623px" }}
                      placeholder="Type Your Comment"
                      name="comment"
                      value={addComment.comment}
                      onChange={onCommentChangeHandler}
                    />
                  </div>
                  <div>
                    <button
                      type="submit"
                      style={{
                        fontSize: "15px",
                        marginLeft: "8px",
                        marginTop: "5px",
                        height: "35px",
                        width: "77px",
                        borderRadius: "28px",
                      }}
                      className="buttons"
                      onClick={(value) => onCommentSubmitHandler(post.postId)}
                    >
                      Add
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Feeds;
