import { MicNone } from "@material-ui/icons";
import React from "react";

const SearchResult = ({ data, onfollowHandler, isFollowed}) => {
  {console.log(isFollowed)}
  let resultList =  data.map((item, itemIndex) => (
        <div className="col-3">
          <div
            className="card search-result-card shadow"
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
              alignItems:"center",
              marginBottom:"20px",
              borderRadius:"24px"
            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems:"center"
              }}
            >
              <img
                className="search-profile-picture rounded-circle"
                src="https://miro.medium.com/max/1400/0*0fClPmIScV5pTLoE.jpg"
              ></img>
              <div>
                <p style={{ textTransform: "capitalize", fontSize:"20px", marginTop:"10px"}}><b>{item.name}</b></p>
              </div>
            </div>
            <button className={isFollowed == item.id?"btn":"buttons2"} style={{width:"140px"}} onClick={() =>onfollowHandler(item.id, itemIndex)}>{isFollowed == item.id?"Follow":"Followed"}</button>
          </div>
        </div>
  ));
  return (
    <div className="container ">
      <div className="row">
          {resultList}
      </div>
    </div>
  );
};

export default SearchResult;
