import { MicNone } from "@material-ui/icons";
import React from "react";

const SearchResult = ({ data, onfollowHandler}) => {
  let resultList =  data.map((item) => (
        <div className="col-3">
          <div
            className="card search-result-card shadow rounded"
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
              alignItems:"center"

            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems:"center"
              }}
            >
              <img
                className="search-profile-picture"
                src="https://miro.medium.com/max/1400/0*0fClPmIScV5pTLoE.jpg"
              ></img>
              <div>
                <p style={{ textTransform: "capitalize", fontSize:"20px"}}><b>{item.name}</b></p>
              </div>
            </div>
            <button className="buttons2" style={{width:"140px"}} onClick={() =>onfollowHandler(item)}>Follow</button>
          </div>
        </div>
  ));
  return (
    <div className="container ">
      <div className="row">
          {resultList}
      </div>
    </div>
  );
};

export default SearchResult;
