import React, { Component } from "react";
import Feeds from "../components/Feeds";
import axios from "axios";
import { Redirect } from "react-router-dom";

class FeedsContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: {},
      posts: [],
      flag: false,
    };
  }

  componentDidMount() {
    axios
      .get('https://jsonplaceholder.typicode.com/posts')
      .then((response) => {
          this.setState({ posts: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }


  onCommentChangeHandler = (event) => {
    this.setState({flag:true});
  };

  onLikeClickHandle = (postId)=>{
    const detail = {id: sessionStorage.getItem("id")};
    axios.post('http://localhost:8080/post/'+postId+'/like', detail)
    .then(response =>{
        console.log(response);
    })
    .catch(error =>{
        console.log(error);
    })
  }

  render() {
    const { data, posts, flag } = this.state;

    return (
        <Feeds posts={posts} onLikeClickHandle ={this.onLikeClickHandle} onCommentClickHandle={this.onC} flag={flag}/>
    );
  }
}

export default FeedsContainer;
