import React from "react";
import {
  Buttons,
  TextBox,
  UploadButton,
  TextArea,
} from "../../../shared/components";
import "../../.././assets/index.css";

const CreatePost = ({
  data,
  onSubmitHandler,
  onChangeHandler,
  handleUpload,
  handleChange,
  imageBool,
  textBool,
  textClick,
  imageClick
}) => {
  return (
    <div className="createPost card" style={{marginTop:"100px", borderRadius:"20px"}}>
      <nav class="nav">
        <img src={process.env.PUBLIC_URL + '/gallery.png'} s></img>
        <a onClick={textClick} className="nav-link" style={{}} aria-current="page" href="#">
          Text
        </a>
        <img src={process.env.PUBLIC_URL + '/writing.png'}></img>
        <a onClick={imageClick} class="nav-link" href="#">
          Image
        </a>
      </nav>
      {textBool && <form onSubmit={onSubmitHandler}>
        <div className="createPost-text" id="defaultOpen">
          <TextArea
            placeholder="Start Writing Post..."
            style={{ height: "100px" }}
            name="text"
            value={data.text}
            onChange={onChangeHandler}
          />
        </div>
        <div className="createPost-buttons">
          <Buttons type="submit" value="Done" />
        </div>
      </form>}

      {imageBool && <div>
        <img></img>
        <div className="createPost-buttons">
          <UploadButton onChange={handleChange} style={{ color: "crimson" }} />
          <Buttons onSubmitHandler={handleUpload} type="submit" value="Done" />
        </div>
      </div>}

    </div>
  );
};

export default CreatePost;
