import { colors } from "@material-ui/core";
import { Button } from "bootstrap";
import React from "react";
import "../../.././assets/index.css";
import { Buttons } from "../../../shared/components";
import SearchResults from "./SearchResult";

debugger;
const SearchBar = ({
  name,
  data,
  onChangeHandler,
  onSubmitHandler,
  error,
  flag,
}) => {
  return (
    <div className="searchBar">
      <div
        style={{
          height: "40px",
          display: "flex",
          flexDirection: "row",
          marginTop: "19px",
          marginLeft: "41px",
        }}
      >
        <label htmlFor="header-search" style={{ borderRadius: "20px" }}>
          <span className="visually-hidden">Search for People</span>
        </label>
        <input
          type="text"
          className="header-top_searchBar{"
          id="header-search"
          placeholder="Search for People..."
          name="name"
          value={name}
          style={{
            width: "400px",
            height: "40px",
            padding: "5px",
            paddingLeft: "15px",
            borderRadius: "20px",
            border: "1px solid black",
          }}
          onChange={onChangeHandler}
        />
        <button
          className="buttons2"
          value="Search"
          style={{
            height: "40px",
            fontSize: "20px",
            background: "white",
            borderColor: "crimson",
            color: "crimson",
            marginLeft: "10px",
          }}
          onClick={onSubmitHandler}
          data-bs-toggle="modal"
          data-bs-target="#staticBackdrop"
        >
          Search
        </button>
        {/* {error !== ""?<div>
          <div class="modal-dialog modal-dialog-scrollable">
            <searchResults error={error} data={data}/>
          </div>
        </div>:<div>
          <div class="modal-dialog modal-dialog-scrollable">
           {data.map((item, itemIndex) =><searchResults data={item}/>)}
          </div>
        </div>} */}

      </div>
    </div>
  );
};
export default SearchBar;
