import React, { Component } from "react";
import Profile from "../components";
import axios from "axios";
import { Redirect } from "react-router-dom";
import { storage } from "../../../utils/helper";

class ProfileContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: { name: "", dob: "",profilePictureUrl:"", portfolioUrl:""},
      experienceDetails: { organisationName: "", startDate: "", endDate: "" },
      educationDetails: {
        degreeName: "",
        major: "",
        institutionName: "",
        marks: "",
        startDate: "",
        endDate: "",
      },
      flag: false,
      imgSrc:"",
      image:"",
      isTyping:true,
      isSubmitting:false,
      portfolio:"",
      portfolioSrc:"",
      isPortfolioUploaded:false,
      isImageUploaded:false
    };
  }

  componentDidMount() {
    axios
      .get(
        "http://localhost:8080/user/" +
          localStorage.getItem("uid") +
          "/profile"
      )
      .then((response) => {
        if (response.data.code == 200) {
          console.log(response)
          this.setState({
            data: {
              name: response.data.data.name,
              dob: response.data.data.dob,

            }
          });
        }

        if(response.data.data.profilePictureUrl !=null){
          this.setState({imgSrc: response.data.data.profilePictureUrl});
        }

        if(response.data.data.portfolioUrl !=null){
          this.setState({portfolioSrc: response.data.data.profilePictureUrl});
        }

        if(response.data.data.educationDetails != null){
          this.setState({educationDetails:response.data.data.educationDetails, });
        }

        if(response.data.data.experienceDetails !=null){
          this.setState({experienceDetails:response.data.data.experienceDetails});
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }

  onExperienceChangeHandler = (event) => {
    let experienceDetails = this.state.experienceDetails;
    experienceDetails[event.target.name] = event.target.value;
    this.setState({ experienceDetails });
  };

  onEducationChangeHandler = (event) => {
    let educationDetails = this.state.educationDetails;
    educationDetails[event.target.name] = event.target.value;
    this.setState({ educationDetails });
  };

  onChangeHandler = (event) => {
    let data = this.state.data;
    data[event.target.name] = event.target.value;
    this.setState({ data });
  };

  onSubmitHandler = (event) => {
    debugger
    event.preventDefault();
    //this.setState({isSubmitting:true})

    if(this.state.isImageUploaded && this.state.isPortfolioUploaded){
      let details = { name: this.state.data.name,
      profilePictureUrl: this.state.imgSrc,
      dob: this.state.data.dob,
      portfolioUrl: this.state.portfolioSrc,
      experienceDetails: this.state.experienceDetails,
      educationDetails: this.state.educationDetails}

      axios.post("http://localhost:8080/user/" + localStorage.getItem("uid") + "/profile",details)
      .then((response) => {
        if (response.data.code === 200) {
          console.log(response+" response");
          this.setState({ flag: true ,isSubmitting:false});
        }
      })
      .catch((error) => {
        console.log(error);
      });
    }

  };

  handleChange = (event) => {
    console.log(event.target.files[0])
    let file = event.target.files[0];
    if (file) {

      this.setState({ image: file});
      let reader = new FileReader();
      let url = reader.readAsDataURL(file);

      reader.onloadend = function (e) {
          this.setState({
              imgSrc: [reader.result]
          })
        }.bind(this);
          console.log(url)
    }

    let image = this.state.image;
    let userId = localStorage.getItem("uid");
    const uploadTask = storage.ref(`images/${userId}/${image.name}`).put(image);
    if(image !== ""){
      uploadTask.on(
        "state_changed",
        snapshot => {
        },
        error => {
            console.log(error);
        },
        () => {
            storage
                .ref("images")
                .child(image.name)
                .getDownloadURL()
                .then(url => {
                    console.log(url+" Url");
                    this.setState({imgSrc:url, isImageUploaded:true})  
                });
        }
      );
    } 

  };

  uploadPortfolioChangeHandler = (event) =>{
    console.log(event.target.files[0]+"portfolio")
    let file = event.target.files[0];
    this.setState({ portfolio: file});
    let portfolio = this.state.portfolio;
    let userId = localStorage.getItem("uid");

    const uploadPortfolio = storage.ref(`portfolio/${userId}/${portfolio.name}`).put(portfolio);
    if(portfolio !== ""){
      uploadPortfolio.on(
        "state_changed",
        snapshot => {
        },
        error => {
            console.log(error);
        },
        () => {
            storage
                .ref("portfolio")
                .child(portfolio.name)
                .getDownloadURL()
                .then(url => {
                    console.log(url);
                    console.log(url+" Url");
                    this.setState({portfolioSrc:url, isPortfolioUploaded:true});  
                });
        }
      );
    } 
  }


  render() {
    const { data, experienceDetails, educationDetails, flag, imgSrc , isSubmitting, portfolioSrc} = this.state;

    if (flag) {
      return(<Redirect to="/feeds" />);
    }
    return (
      <>
        <Profile
          detail={data}
          educationDetails={educationDetails}
          experienceDetails={experienceDetails}
          onSubmitHandler={this.onSubmitHandler}
          onChangeHandler={this.onChangeHandler}
          onEducationChangeHandler={this.onEducationChangeHandler}
          onExperienceChangeHandler={this.onExperienceChangeHandler}
          handleChange = {this.handleChange}
          imgSrc = {imgSrc}
          isSubmitting = {isSubmitting}
          uploadPortfolioChangeHandler = {this.uploadPortfolioChangeHandler}
          portfolioSrc = {portfolioSrc}
        />
      </>
    );
  }
}

export default ProfileContainer;
