import {SearchResult, SearchBar} from "../components";
import React, { Component } from "react";
import axios from "axios";

class SearchResultContainer extends Component {

    constructor(props) {
        super(props);

        this.state = {
          data: [],
          name: "",
          error:""
        };
      }

    onChangeHandler = (event) => {

      let name = this.state.name;
      name = event.target.value;
      this.setState({name:name});
      console.log(name);
    };

    onSubmitHandler = (event) =>{

    axios.get("http://localhost:8080/user?name="+this.state.name)
        .then((response) => {
          if (response.data.code == 200) {
              this.setState({data:response.data.data.usersList});
              let mdata = data.map((item) => {

              })
          }else{
              this.setState({data:[], error:response.data.msg});
          }
        })
        .catch((error) => {
          console.log(error);
        });
    }

    onFollowHandler = (userId, itemIndex) =>{

      let detail = {followeeId:userId};

      axios.post(`http://localhost:8080/user/${localStorage.getItem("uid")}/following`, detail)
      .then((responce) => {

          if(responce.data.code == 200){
            let arr = this.state.data;
            let obj = arr[itemIndex];
            obj["isfollowed"] = true;
            arr[itemIndex] = obj;
            this.setState({data:arr});
            console.log(responce);
          }
      })
      .catch((error) => {
        console.log(error);
      });
    }

    render() {
        const { data, name, error} = this.state;

        return (
          <div className="feedMain">
            <SearchBar
              name={name}
              onChangeHandler={this.onChangeHandler}
              onSubmitHandler={this.onSubmitHandler}
            />
            <div style={{marginTop:"50px"}}></div>
            {data.length !== 0?<SearchResult data={data} onfollowHandler ={this.onFollowHandler} />:<div><p>{error}</p></div>}
          </div>
        );
      }
}

export default SearchResultContainer;
