import CreatePostContainer from "./CreatePost";
import AddCommentContainer from "./AddComment";
import FeedsContainer from "./Feeds";

import React, { Component } from "react";
import FeedMain from "../components";
import { SearchBar } from "../../../shared/components";

class FeedMainContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [],
      name: "",
    };
  }

  onChangeHandler = (event) => {
    let name = this.state.name;
    name = event.target.value;
    this.setState(name);
  };

  onSubmitHandler = (event) =>{
    event.preventDefault();
    axios.post("http://localhost:8080/user?"+this.state.name)
      .then((response) => {
        if (response.data.code == 200) {
          this.setState(data, response.data.data);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    const { data, name } = this.state;
    return (
      <div className="feedMain">
        <SearchBar
          name={name}
          onChangeHandler={this.onChangeHandler}
          onSubmitHandler={this.onSubmitHandler}
        />
        <CreatePostContainer />
        <FeedsContainer />
      </div>
    );
  }
}

export default FeedMainContainer;
