import React from "react";

const CommentLayout = ({comment, commentBy, created}) =>{

    return (
        <div style={{display:"flex", flexDirection:"row"}}>
            <img className="rounded-circle" style={{width:"5rem", height:"5rem"}} src="https://www.freeiconspng.com/thumbs/profile-icon-png/profile-icon-9.png"></img>
            <div class="card" style={{width: "24rem", marginBottom:"5px", borderRadius:"25px", padding:"10px"}}>
                <div class="card-body" style={{padding:"5px"}}>
                    <div style={{display:"flex", flexDirection:"row", just}}></div>
                    <h5 class="card-title" style={{color:"crimson", textTransform:"capitalize"}}>{commentBy}</h5>
                    <span>{created}</span>
                    <p class="card-text" style={{fontWeight:"100px", fontSize:"15px"}}>{comment}</p>
                </div>
            </div>
        </div>
    );
}

export default CommentLayout;
