import React from "react";
import {
  Buttons,
  TextBox,
  UploadButton,
  TextArea,
} from "../../../shared/components";
import "../../.././assets/index.css";

const CreatePost = ({
  data,
  onSubmitHandler,
  onChangeHandler,
  handleUpload,
  handleChange,
  imageBool,
  textBool,
  textClick,
  imageClick
}) => {
  return (
    <div className="createPost card">
      <nav class="nav">
        <a onClick={textClick} class="nav-link active" aria-current="page" href="#">
          Text
        </a>
        <a onClick={imageClick} class="nav-link" href="#">
          Image
        </a>
      </nav>
      {textBool && <form onSubmit={onSubmitHandler}>
        <div className="createPost-text">
          <TextArea
            placeholder="Start Writing Post..."
            style={{ height: "100px" }}
            name="text"
            value={data.text}
            onChange={onChangeHandler}
          />
        </div>
        <div className="createPost-buttons">
          <UploadButton onChange={handleChange} style={{ color: "crimson" }} />
          <Buttons type="submit" value="Done" />
        </div>
      </form>}

      {textBool && <form onSubmit={onSubmitHandler}>
        <div className="createPost-text">
          <TextArea
            placeholder="Start Writing Post..."
            style={{ height: "100px" }}
            name="text"
            value={data.text}
            onChange={onChangeHandler}
          />
        </div>
        <div className="createPost-buttons">
          <UploadButton onChange={handleChange} style={{ color: "crimson" }} />
          <Buttons type="submit" value="Done" />
        </div>
      </form>}

    </div>
  );
};

export default CreatePost;
