import React from "react";
import {
  Buttons,
  TextBox,
  UploadButton,
  TextArea,
} from "../../../shared/components";
import "../../.././assets/index.css";

const CreatePost = ({
  data,
  onSubmitHandler,
  onChangeHandler,
  handleUpload,
  handleChange,
  imageBool,
  textBool,
  textClick,
  imageClick,
  imgSrc,
}) => {
  return (
    <div
      className="createPost card"
      style={{ marginTop: "100px", borderRadius: "20px" }}
    >
      <nav class="nav" style={{ marginTop: "3px" }}>
        <div
          className="align-row post-action-background create-post-action"
          onClick={textClick}
          style={{ borderBottom: textBool ? "1px solid grey" : "" }}
        >
          <img
            className="rounded-circle"
            src={process.env.PUBLIC_URL + "/writing.png"}
            style={{ width: "28px", height: "28px" }}
          ></img>
          <a className="nav-link" aria-current="page" href="#">
            Text
          </a>
        </div>

        <div
          className="align-row post-action-background create-post-action"
          style={{ marginLeft: "10px" }}
          onClick={imageClick}
          style={{
            marginLeft: "5px",
            borderBottom: imageBool ? "1px solid grey" : "",
          }}
        >
          <img
            src={process.env.PUBLIC_URL + "/gallery.png"}
            style={{ width: "28px", height: "28px" }}
          ></img>
          <a class="nav-link" href="#">
            Image
          </a>
        </div>
      </nav>
      <hr></hr>
      {textBool && (
        <form onSubmit={onSubmitHandler}>
          <div className="createPost-text" id="defaultOpen">
            <TextArea
              placeholder="Start Writing Post..."
              style={{ height: "100px" }}
              name="text"
              value={data.text}
              onChange={onChangeHandler}
            />
          </div>
          <div className="createPost-buttons">
            <Buttons type="submit" value="Done" />
          </div>
        </form>
      )}

      {imageBool && (
        <div>
          <img className = "postImage" src={imgSrc}></img>
          <div className="createPost-buttons">
            <UploadButton
              onChange={handleChange}
              style={{ color: "crimson" }}
            />
            <Buttons
              onSubmitHandler={handleUpload}
              type="submit"
              value="Done"
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default CreatePost;
