import React, { Component } from "react";
import Login from "../components";
import axios from "axios";
import { Redirect } from "react-router-dom";

class LoginContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: {},
      loggedIn:false
    };
  }

  onChangeHandler = (event) => {
    let data  = this.state.data;
    data[event.target.name] = event.target.value;
    this.setState(data);
  };

  onSubmitHandler = (event) => {
    event.preventDefault();
    axios.post('http://localhost:8080/user/login', this.state.data)
    .then(response =>{
        if(response.data.code == 200){
          localStorage.setItem("isAuth",true);
          console.log(response);
          localStorage.setItem("uid",response.data.data.userId);
          this.setState({loggedIn:true});
        }
    })
    .catch(error => {
        console.log(error);
    });
  };


  render() {
    const { data, loggedIn} = this.state;

    if(loggedIn){
      return (
        <Redirect to="/feeds"/>
      );
    }
    return (
      <>
      <div style={{display:"flex", justifyContent:"center", marginTop:"30px", color:"red", marginBottom:"20px"}}>
        <div><b>{this.state.error}</b></div>
      </div>
      <Login
        state={data}
        onSubmitHandler={this.onSubmitHandler}
        onChangeHandler={this.onChangeHandler}
      />
      </>
    );
  }
}

export default LoginContainer;
